for (const item of items) {
  const html = item.json.data;
  const preferredLang = item.json.language; // optional 사용
  const match = html.match(/"captionTracks":\[(.*?)\]/);

  if (!match) {
    item.json = { error: 'Transcript not found (no captionTracks)' };
    continue;
  }

  const tracks = JSON.parse(`[${match[1]}]`);

  // 언어 지정이 있으면 해당 언어 우선
  let track = null;

  if (preferredLang) {
    track = tracks.find(t =>
      (t.languageCode === preferredLang || t.vssId?.includes(`.${preferredLang}`))
    );
  }

  // 지정 언어가 없거나 찾지 못했으면: 수동 자막 → 자동 자막 순서로 아무거나
  if (!track) {
    track = tracks.find(t => !t.kind) || tracks.find(t => t.kind === 'asr');
  }

  item.json.transcriptUrl = track?.baseUrl || null;
  item.json.transcriptLang = track?.languageCode || null;
}

return items;

const xmlString = $input.first().json.data;
const youtubeUrl = $input.first().json.youtubeUrl;

const lang = 'ko';
// 매우 간단한 XML 파서 (DOMParser 없이 작동)
const matches = [...xmlString.matchAll(/<text start="([^"]+)" dur="([^"]+)">([\s\S]*?)<\/text>/g)];

const transcript = matches.map(match => ({
  text: match[3].replace(/&amp;/g, '&').replace(/&quot;/g, '"').replace(/&lt;/g, '<').replace(/&gt;/g, '>'),
  duration: parseFloat(match[2]),
  offset: parseFloat(match[1]),
  lang: lang
}));

return [
  {
    json: {
      youtubeId: youtubeUrl,
      transcript: transcript
    }
  }
];

options.systemMessage:
=당신은 유튜브 영상의 자막을 읽고, 핵심 메시지를 잘 전달하는 콘텐츠 요약 작가입니다.

- 자막 내용을 기반으로 영상이 전달하고자 하는 **주제, 주장, 논리 전개 순서**를 구조화된 형태로 요약해 주세요.
- 시청자가 영상을 보지 않아도 내용을 빠르게 이해할 수 있도록 요약해 주세요.
- 답변은 반드시 한글로 해야합니다.
- 문단은 다음과 같은 구조로 구성해 주세요:

[JSON OUTPUT FORMAT]
{
  "title": "요약의 제목 또는 주제",
  "summary": "전체 내용을 압축한 핵심 개요",
  "sections": [
    {
      "heading": "소제목 1",
      "content": "해당 섹션의 요약 내용"
    },
    {
      "heading": "소제목 2",
      "content": "이 섹션에서는 관련 개념이나 사례를 설명"
    }
  ]
}
- 각 문단은 자연스럽고 읽기 쉬운 문장으로 작성하고, **중요한 부분은 굵게 강조**해 주세요.
- 이모지를 활용해 각 문단을 시각적으로 구분해 주세요.
- 단순 요약이 아니라 **콘텐츠의 의도와 흐름을 재구성**해 주세요.

